INSERT INTO `eac` VALUES (1,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<eac-cpf xmlns=\"urn:isbn:1-931666-33-4\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ead=\"urn:isbn:1-931666-22-9\" xmlns:owl=\"http://www.w3.org/2002/07/owl#\" xmlns:rdf=\"http://www.w3.org/1999/02/22-rdf-syntax-ns#\" xmlns:eac=\"http://eac.staatsbibliothek-berlin.de/schema/cpf.xsd\" xmlns:php=\"http://php.net/xsl\">\n  <control>\n    <recordId>abella_rosa_m_collection</recordId>\n    <maintenanceStatus>new</maintenanceStatus>\n    <publicationStatus>inProcess</publicationStatus>\n    <maintenanceAgency>\n      <agencyCode>FMU-N</agencyCode>\n      <otherAgencyCode localType=\"oclc\">FQG</otherAgencyCode>\n      <agencyName>University of Miami Libraries</agencyName>\n    </maintenanceAgency>\n    <languageDeclaration>\n      <language languageCode=\"eng\">English</language>\n      <script scriptCode=\"Latn\">Latin</script>\n    </languageDeclaration>\n    <maintenanceHistory>\n      <maintenanceEvent>\n        <eventType>created</eventType>\n        <eventDateTime standardDateTime=\"2013-06-27\"/>\n        <agentType>machine</agentType>\n        <agent>ead2eac.xsl</agent>\n      </maintenanceEvent>\n    </maintenanceHistory>\n    <sources>\n      <source xlink:type=\"simple\" xlink:href=\"http://proust.library.miami.edu/findingaids/?p=collections/findingaid&amp;id=160\">\n        <sourceEntry>Rosa M. Abella Collection, 1996-1997</sourceEntry>\n      </source>\n    </sources>\n  </control>\n  <cpfDescription>\n    <identity>\n      <entityType>person</entityType>\n      <nameEntry>\n        <part>Abella, Rosa M.</part>\n        <authorizedForm>local</authorizedForm>\n      </nameEntry>\n    </identity>\n    <description>\n      <biogHist>\n        <p>Rosa M. Abella was an exiled Cuban librarian who worked at the University of Miami Otto G. Richter Library.</p>\n        <p>A native of Havana, Abella received her library technician degree in 1955 and a doctor of philosophy in 1958. She served as the head of the circulation department for the National Library of Cuba from 1960 to 1961, at which point she left the island for Miami, Florida, as a political refugee. She worked as a librarian at Assumption Academy until she was hired as an acquisitions librarian for the Otto G. Richter Library in 1962, specializing in Spanish and Hispanic materials.</p>\n        <p>Abella was instrumental in the preservation of Cuban materials and the founding of the library\'s Cuban Heritage Collection.</p>\n      </biogHist>\n    </description>\n    <relations>\n      <resourceRelation xlink:arcrole=\"creatorOf\" xlink:href=\"http://proust.library.miami.edu/findingaids/?p=collections/findingaid&amp;id=\" xlink:role=\"archivalRecords\" xlink:type=\"simple\">\n        <relationEntry>Rosa M. Abella Collection 1996-1997</relationEntry>\n        <objectXMLWrap>\n          <ead xmlns=\"urn:isbn:1-931666-22-9\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" audience=\"external\" xsi:schemaLocation=\"http://www.loc.gov/ead/ http://www.loc.gov/ead/ead.xsd\">\n            <eadheader langencoding=\"iso639-2b\" audience=\"external\" countryencoding=\"iso3166-1\" dateencoding=\"iso8601\" repositoryencoding=\"iso15511\" scriptencoding=\"iso15924\" relatedencoding=\"MARC21\">\n              <eadid encodinganalog=\"856$u\" mainagencycode=\"US-FQG\" countrycode=\"US\" identifier=\"ArchonInternalCollectionID:160\">01/CHC0003</eadid>\n              <filedesc>\n                <titlestmt>\n                  <titleproper encodinganalog=\"245\">Guide to the Rosa M. Abella Collection</titleproper>\n                  <titleproper type=\"filing\">Abella, Rosa M. Collection</titleproper>\n                  <author encodinganalog=\"245$c\">Finding Aid Authors: Amanda Moreno.</author>\n                </titlestmt>\n                <publicationstmt>\n                  <p>Â© Copyright 2013 University of Miami Cuban Heritage Collection.  All rights reserved.</p>\n                  <address>\n                    <addressline>University of Miami Libraries</addressline>\n                    <addressline>1300 Memorial Drive</addressline>\n                    <addressline>Coral Gables, FL, 33124</addressline>\n                    <addressline>URL: http://library.miami.edu/chc/</addressline>\n                    <addressline>Email: chc@miami.edu</addressline>\n                    <addressline>Phone: (305) 284-4900</addressline>\n                    <addressline>Fax: (305) 284-4901</addressline>\n                  </address>\n                </publicationstmt>\n              </filedesc>\n              <profiledesc>\n                <creation encodinganalog=\"500\">This finding aid was encoding in EAD by Archon 3.21 from an SQL database source on <date type=\"encoded\" normal=\"2013-04-18\">April 18th, 2013</date>.</creation>\n                <langusage encodinganalog=\"546\">The collection description/finding aid is written in <language encodinganalog=\"041\" langcode=\"eng\">English</language></langusage>\n              </profiledesc>\n            </eadheader>\n            <frontmatter>\n              <titlepage>\n                <titleproper encodinganalog=\"245\">Guide to the Rosa M. Abella Collection\n                              <date normal=\"1996/1997\" encodinganalog=\"260$c\">1996/1997</date></titleproper>\n                <publisher encodinganalog=\"260$b\">University of Miami Cuban Heritage Collection</publisher>\n              </titlepage>\n            </frontmatter>\n            <archdesc level=\"collection\" type=\"inventory\" audience=\"external\" relatedencoding=\"MARC21\">\n              <did>\n                <head>Overview of the Collection</head>\n                <unittitle label=\"Collection Title\" encodinganalog=\"245\">Rosa M. Abella Collection                              <unitdate label=\"Dates\" encodinganalog=\"245$f\" type=\"inclusive\" normal=\"1996/1997\">1996-1997</unitdate></unittitle>\n                <unitid encodinganalog=\"035\" label=\"Identification\" repositorycode=\"US-FQG\" countrycode=\"US\">01/CHC0003</unitid>\n                <origination label=\"Creator\" encodinganalog=\"245$c\">\n                  <persname encodinganalog=\"100\" normal=\"Abella, Rosa M.\" source=\"local\" role=\"Collector\">Abella, Rosa M.</persname>\n                </origination>\n                <physdesc label=\"Physical Description\">\n                  <extent encodinganalog=\"300\" type=\"Linear Feet\">2.00</extent>\n                </physdesc>\n                <physdesc label=\"Alternate Extent Statement\" encodinganalog=\"300$a\">18.20000076</physdesc>\n                <repository encodinganalog=\"852$b\" label=\"Repository\">\n                  <corpname>University of Miami Cuban Heritage Collection</corpname>\n                  <address>\n                    <addressline>University of Miami Libraries</addressline>\n                    <addressline>1300 Memorial Drive</addressline>\n                    <addressline>Coral Gables, FL, </addressline>\n                    <addressline>URL: http://library.miami.edu/chc/</addressline>\n                    <addressline>Email: chc@miami.edu</addressline>\n                    <addressline>Phone: (305) 284-4900</addressline>\n                    <addressline>Fax: (305) 284-4901</addressline>\n                  </address>\n                </repository>\n                <abstract encodinganalog=\"520$a\" label=\"Abstract\">The Rosa M. Abella Collection contains archival materials related to the 1996 shootdown of Brothers to the Rescue planes over international waters between the United States and Cuba.</abstract>\n                <note encodinganalog=\"500\" label=\"Note\">\n                  <p>Other Information:</p>\n                  <p>Sponsor Note: Selections from this collection were digitized with a grant from the State of Florida Department of State, Division of Cultural Affairs, and the Florida Arts Council in partnership with the National Endowment for the Arts.</p>\n                </note>\n              </did>\n              <!--COLLECTION LEVEL METADATA: -->\n              <bioghist altrender=\"Biographical Information\" encodinganalog=\"545\">\n                <head>Biographical Information:</head>\n                <p>Rosa M. Abella was an exiled Cuban librarian who worked at the University of Miami Otto G. Richter Library.</p>\n                <p>A native of Havana, Abella received her library technician degree in 1955 and a doctor of philosophy in 1958. She served as the head of the circulation department for the National Library of Cuba from 1960 to 1961, at which point she left the island for Miami, Florida, as a political refugee. She worked as a librarian at Assumption Academy until she was hired as an acquisitions librarian for the Otto G. Richter Library in 1962, specializing in Spanish and Hispanic materials.</p>\n                <p>Abella was instrumental in the preservation of Cuban materials and the founding of the library\'s Cuban Heritage Collection.</p>\n              </bioghist>\n              <!-- CONTROLLED ACCESS / SUBJECT TERMS -->\n              <!-- END CONTROLLED ACCESS TERMS -->\n              <!-- DIGITAL ARCHIVAL OBJECTS -->\n              <dao xlink:type=\"simple\" xlink:href=\"http://tinyurl.com/yjdcnhs\" xlink:actuate=\"onRequest\" xlink:show=\"new\">\n                <daodesc>\n                  <head>Title:</head>\n                  <p>Rosa Abella Collection (digital collection)</p>\n                </daodesc>\n              </dao>\n              <!-- END DIGITAL ARCHIVAL OBJECTS -->\n              <!-- ADMINISTRATIVE INFORMATION -->\n              <descgrp>\n                <head>Administrative Information</head>\n                <acqinfo encodinganalog=\"541\">\n                  <head>Acquisition Information:</head>\n                  <p>Gifts of Rosa M. Abella, 2001 and 2006.</p>\n                </acqinfo>\n                <processinfo encodinganalog=\"583\">\n                  <head>Processing Information:</head>\n                  <p>Minimal record added by Amanda Moreno, January 2013.</p>\n                </processinfo>\n                <separatedmaterial encodinganalog=\"544 0\">\n                  <head>Separated Materials:</head>\n                  <p>Photographs from this collection are housed in the photograph section of the compact files.</p>\n                </separatedmaterial>\n                <appraisal encodinganalog=\"583\">\n                  <head>Appraisal Information:</head>\n                  <p>A second donation in 2006 added photographs and Lyceum and Lawn Tennis clippings to the collection.</p>\n                </appraisal>\n                <userestrict encodinganalog=\"540\">\n                  <head>Conditions Governing Use:</head>\n                  <p>Requests to publish or display materials from this collection require written permission from the rights owner. Please contact chc@miami.edu for more information.</p>\n                </userestrict>\n                <accessrestrict encodinganalog=\"506\">\n                  <head>Conditions Governing Access:</head>\n                  <p>This collection is open for research.</p>\n                </accessrestrict>\n                <altformavail encodinganalog=\"530\">\n                  <head>Electronic Format:</head>\n                  <p>Selected materials from this collection have been digitized.</p>\n                  <p>http://tinyurl.com/yjdcnhs</p>\n                </altformavail>\n                <prefercite encodinganalog=\"524\">\n                  <head>Preferred Citation:</head>\n                  <p>Rosa M. Abella Collection, Cuban Heritage Collection, University of Miami Libraries, Coral Gables, Florida</p>\n                </prefercite>\n                <relatedmaterial encodinganalog=\"544 1\">\n                  <head>Related Materials:</head>\n                  <p>CHC5117 Phonograph Collection</p>\n                  <p>Information about related materials is available at http://proust.library.miami.edu/findingaids/?p=collections/controlcard&amp;id=454</p>\n                </relatedmaterial>\n              </descgrp>\n              <scopecontent>\n                <head>Scope and Contents</head>\n                <p>The Rosa M. Abella Collection contains clippings, other archival materials and a bibliography, co-authored with Dolores Rovirosa, describing the shootdown of two Brothers to the Rescue planes in 1996.</p>\n                <p>The collection also contains photographs and clippings related to Lyceum and Lawn Tennis.</p>\n              </scopecontent>\n              <!-- END ADMINISTRATIVE INFORMATION -->\n              <!-- END COLLECTION LEVEL METADATA -->\n              <!-- END SUBORDINATE COMPONENTS -->\n            </archdesc>\n          </ead>\n        </objectXMLWrap>\n      </resourceRelation>\n    </relations>\n  </cpfDescription>\n</eac-cpf>\n',0,'/Applications/MAMP/htdocs/eac/ead/abella_rosa_m_collection.xml','2013-06-27 15:27:26','2013-06-27 11:27:26'),(2,'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<eac-cpf xmlns=\"urn:isbn:1-931666-33-4\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:ead=\"urn:isbn:1-931666-22-9\" xmlns:owl=\"http://www.w3.org/2002/07/owl#\" xmlns:rdf=\"http://www.w3.org/1999/02/22-rdf-syntax-ns#\" xmlns:eac=\"http://eac.staatsbibliothek-berlin.de/schema/cpf.xsd\" xmlns:php=\"http://php.net/xsl\">\n  <control>\n    <recordId>acosta_magali_o_collection_19561960</recordId>\n    <maintenanceStatus>new</maintenanceStatus>\n    <publicationStatus>inProcess</publicationStatus>\n    <maintenanceAgency>\n      <agencyCode>FMU-N</agencyCode>\n      <otherAgencyCode localType=\"oclc\">FQG</otherAgencyCode>\n      <agencyName>University of Miami Libraries</agencyName>\n    </maintenanceAgency>\n    <languageDeclaration>\n      <language languageCode=\"eng\">English</language>\n      <script scriptCode=\"Latn\">Latin</script>\n    </languageDeclaration>\n    <maintenanceHistory>\n      <maintenanceEvent>\n        <eventType>created</eventType>\n        <eventDateTime standardDateTime=\"2013-06-27\"/>\n        <agentType>machine</agentType>\n        <agent>ead2eac.xsl</agent>\n      </maintenanceEvent>\n    </maintenanceHistory>\n    <sources>\n      <source xlink:type=\"simple\" xlink:href=\"http://proust.library.miami.edu/findingaids/?p=collections/findingaid&amp;id=55\">\n        <sourceEntry>Magali O. Acosta Collection, 1956-1960</sourceEntry>\n      </source>\n    </sources>\n  </control>\n  <cpfDescription>\n    <identity>\n      <entityType>person</entityType>\n      <nameEntry>\n        <part>Prats Llorens, Rodrigo, 1909-1980Acosta, Magali O.</part>\n        <authorizedForm>lcnaf</authorizedForm>\n      </nameEntry>\n    </identity>\n    <description>\n      <biogHist>\n        <p>Rodrigo Prats was one of Cuba\'s most important composers of zarzuelas (Spanish operetta). Born in Sagua la Grande, Cuba on 7 February 1909, Prats was the son of another important Cuban composer and musician, Jaime Prats. Violinist, pianist, composer, arranger, and orchestral director, Rodrigo Prats directed several orchestras in Havana and served as musical director for radio and TV stations. Prats gained notoriety with the publication â€œUna rosa de Francia,â€ a criolla-bolero he composed at age 15. His body of work includes popular music, sainetes, and zarzuelas. Among his most important zarzuelas is \"Amalia Batista\", and many artists recorded his songs.</p>\n        <p>Rodrigo Prats joined the faculty of Havana\'s Studio Sylvia M. Goudie in 1956 after his stint at the Iranzo Conservatory. He died in Havana on 15 September 1980.</p>\n      </biogHist>\n    </description>\n    <relations>\n      <resourceRelation xlink:arcrole=\"creatorOf\" xlink:href=\"http://proust.library.miami.edu/findingaids/?p=collections/findingaid&amp;id=\" xlink:role=\"archivalRecords\" xlink:type=\"simple\">\n        <relationEntry>Magali O. Acosta Collection 1956-1960</relationEntry>\n        <objectXMLWrap>\n          <ead xmlns=\"urn:isbn:1-931666-22-9\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" audience=\"external\" xsi:schemaLocation=\"http://www.loc.gov/ead/ http://www.loc.gov/ead/ead.xsd\">\n            <eadheader langencoding=\"iso639-2b\" audience=\"external\" countryencoding=\"iso3166-1\" dateencoding=\"iso8601\" repositoryencoding=\"iso15511\" scriptencoding=\"iso15924\" relatedencoding=\"MARC21\">\n              <eadid encodinganalog=\"856$u\" mainagencycode=\"US-FQG\" countrycode=\"US\" identifier=\"ArchonInternalCollectionID:55\">01/CHC5145</eadid>\n              <filedesc>\n                <titlestmt>\n                  <titleproper encodinganalog=\"245\">Guide to the Magali O. Acosta Collection</titleproper>\n                  <titleproper type=\"filing\">Acosta, Magali O. Collection, 1956-1960</titleproper>\n                  <author encodinganalog=\"245$c\">Finding Aid Authors: MarÃ­a R. Estorino, September 2004.</author>\n                </titlestmt>\n                <publicationstmt>\n                  <p>Â© Copyright 2013 University of Miami Cuban Heritage Collection.  All rights reserved.</p>\n                  <address>\n                    <addressline>University of Miami Libraries</addressline>\n                    <addressline>1300 Memorial Drive</addressline>\n                    <addressline>Coral Gables, FL, 33124</addressline>\n                    <addressline>URL: http://library.miami.edu/chc/</addressline>\n                    <addressline>Email: chc@miami.edu</addressline>\n                    <addressline>Phone: (305) 284-4900</addressline>\n                    <addressline>Fax: (305) 284-4901</addressline>\n                  </address>\n                </publicationstmt>\n              </filedesc>\n              <profiledesc>\n                <creation encodinganalog=\"500\">This finding aid was encoding in EAD by Archon 3.21 from an SQL database source on <date type=\"encoded\" normal=\"2013-04-18\">April 18th, 2013</date>.</creation>\n                <langusage encodinganalog=\"546\">The collection description/finding aid is written in <language encodinganalog=\"041\" langcode=\"eng\">English</language></langusage>\n              </profiledesc>\n              <revisiondesc>\n                <change encodinganalog=\"583\">\n                  <date type=\"encoded\" normal=\"2013-04-18\">April 18th, 2013</date>\n                  <item>Revisions to the collection-level record made by Beata Bergen, June 2009. Biographical and Scope and Contents notes revised by Matt Taylor in Fall 2010 as part of a musicology seminar on Latin American music collections taught by Dr. Deborah Schwartz-Kates of the University of Miami Frost School of Music.</item>\n                </change>\n              </revisiondesc>\n            </eadheader>\n            <frontmatter>\n              <titlepage>\n                <titleproper encodinganalog=\"245\">Guide to the Magali O. Acosta Collection\n                              <date normal=\"1956/1960\" encodinganalog=\"260$c\">1956/1960</date></titleproper>\n                <publisher encodinganalog=\"260$b\">University of Miami Cuban Heritage Collection</publisher>\n              </titlepage>\n            </frontmatter>\n            <archdesc level=\"collection\" type=\"inventory\" audience=\"external\" relatedencoding=\"MARC21\">\n              <did>\n                <head>Overview of the Collection</head>\n                <unittitle label=\"Collection Title\" encodinganalog=\"245\">Magali O. Acosta Collection                              <unitdate label=\"Dates\" encodinganalog=\"245$f\" type=\"inclusive\" normal=\"1956/1960\">1956-1960</unitdate></unittitle>\n                <unitid encodinganalog=\"035\" label=\"Identification\" repositorycode=\"US-FQG\" countrycode=\"US\">01/CHC5145</unitid>\n                <origination label=\"Creator\" encodinganalog=\"245$c\">\n                  <persname encodinganalog=\"100\" normal=\"Prats Llorens, Rodrigo\" source=\"lcnaf\" role=\"Collector\">Prats Llorens, Rodrigo, 1909-1980</persname>\n                  <persname encodinganalog=\"100\" normal=\"Acosta, Magali O.\" source=\"local\" role=\"Collector\">Acosta, Magali O.</persname>\n                </origination>\n                <physdesc label=\"Physical Description\">\n                  <extent encodinganalog=\"300\" type=\"Boxes\">1.00</extent>\n                </physdesc>\n                <langmaterial encodinganalog=\"546\" label=\"Language of Materials\">\n                  <language encodinganalog=\"041\" langcode=\"spa\">Spanish;Castilian</language>\n                </langmaterial>\n                <repository encodinganalog=\"852$b\" label=\"Repository\">\n                  <corpname>University of Miami Cuban Heritage Collection</corpname>\n                  <address>\n                    <addressline>University of Miami Libraries</addressline>\n                    <addressline>1300 Memorial Drive</addressline>\n                    <addressline>Coral Gables, FL, </addressline>\n                    <addressline>URL: http://library.miami.edu/chc/</addressline>\n                    <addressline>Email: chc@miami.edu</addressline>\n                    <addressline>Phone: (305) 284-4900</addressline>\n                    <addressline>Fax: (305) 284-4901</addressline>\n                  </address>\n                </repository>\n              </did>\n              <!--COLLECTION LEVEL METADATA: -->\n              <bioghist altrender=\"Biographical Information\" encodinganalog=\"545\">\n                <head>Biographical Information:</head>\n                <p>Rodrigo Prats was one of Cuba\'s most important composers of zarzuelas (Spanish operetta). Born in Sagua la Grande, Cuba on 7 February 1909, Prats was the son of another important Cuban composer and musician, Jaime Prats. Violinist, pianist, composer, arranger, and orchestral director, Rodrigo Prats directed several orchestras in Havana and served as musical director for radio and TV stations. Prats gained notoriety with the publication â€œUna rosa de Francia<emph render=\"italic\">,â€ </emph>a criolla-bolero he composed at age 15. His body of work includes popular music, sainetes, and zarzuelas. Among his most important zarzuelas is \"Amalia Batista\", and many artists recorded his songs.</p>\n                <p>Rodrigo Prats joined the faculty of Havana\'s Studio Sylvia M. Goudie in 1956 after his stint at the Iranzo Conservatory.  He died in Havana on 15 September 1980.</p>\n              </bioghist>\n              <!-- CONTROLLED ACCESS / SUBJECT TERMS -->\n              <!-- END CONTROLLED ACCESS TERMS -->\n              <!-- DIGITAL ARCHIVAL OBJECTS -->\n              <!-- END DIGITAL ARCHIVAL OBJECTS -->\n              <!-- ADMINISTRATIVE INFORMATION -->\n              <descgrp>\n                <head>Administrative Information</head>\n                <acqinfo encodinganalog=\"541\">\n                  <head>Acquisition Information:</head>\n                  <p>Gift of Magali O. Acosta, 2003</p>\n                </acqinfo>\n                <processinfo encodinganalog=\"583\">\n                  <head>Processing Information:</head>\n                  <p>MarÃ­a R. Estorino, September 2004. Retrospectively converted from HTML to EAD XML September, 2008 by Lyn MacCorkle and Kyle Rimkus based on a crosswalk by MarÃ­a Estorino.</p>\n                </processinfo>\n                <separatedmaterial encodinganalog=\"544 0\">\n                  <head>Separated Materials:</head>\n                  <p><extref linktype=\"simple\" audience=\"external\" show=\"embed\" actuate=\"onrequest\" href=\"http://catalog.library.miami.edu/record=b3420826~S11\"><emph render=\"italic\">Memoria: 1949-1959</emph></extref>, a yearbook of the Sylvia M. Goudie dance studio in Havana, was cataloged in the CHC book collection.</p>\n                </separatedmaterial>\n                <userestrict encodinganalog=\"540\">\n                  <head>Conditions Governing Use:</head>\n                  <p>Requests to publish or display materials from this collection require written permission from the rights owner. Please, contact <extref linktype=\"simple\" audience=\"external\" show=\"embed\" actuate=\"onrequest\" href=\"mailto:chc@miami.edu\">chc@miami.edu</extref> for more information.</p>\n                </userestrict>\n                <accessrestrict encodinganalog=\"506\">\n                  <head>Conditions Governing Access:</head>\n                  <p>This collection is open for research.</p>\n                </accessrestrict>\n                <prefercite encodinganalog=\"524\">\n                  <head>Preferred Citation:</head>\n                  <p>Magali O. Acosta Collection, Cuban Heritage Collection, University of Miami Libraries, Coral Gables, Florida</p>\n                </prefercite>\n              </descgrp>\n              <arrangement encodinganalog=\"351\">\n                <head>Arrangement of Materials:</head>\n                <p>Each folder is dedicated to a specific song<emph render=\"italic\">.</emph> There are seven folders, organized alphabetically by title.</p>\n              </arrangement>\n              <scopecontent>\n                <head>Scope and Contents</head>\n                <p>The Magali O. Acosta Collection contains the handwritten parts and source piano scores for arrangements by the Cuban composer Rodrigo Prats aggregated over a three year period from 1957 to 1960.Â  Acosta and Prats collaborated on a series of dance performances called \"Ballet EspaÃ±ol\", which included works of Ernesto Lecuona and AgustÃ­n Lara, among others, when they both worked for the Sylvia M. Goudie dance studio in Havana.</p>\n                <p>An important supplement to this collection is the 10-year retrospective of the Studio Sylvia M. Goudie, <extref linktype=\"simple\" audience=\"external\" show=\"embed\" actuate=\"onrequest\" href=\"http://catalog.library.miami.edu/record=b3420826~S11\"><emph render=\"italic\">Memoria: 1949-1959</emph></extref>, in which Acosta writes about her collaborations with Prats.</p>\n              </scopecontent>\n              <!-- END ADMINISTRATIVE INFORMATION -->\n              <!-- END COLLECTION LEVEL METADATA -->\n              <!-- BEGIN SUBORDINATE COMPONENTS -->\n              <dsc type=\"combined\">\n                <head>Detailed List of Contents</head>\n                <c01 level=\"series\">\n                  <did>\n                    <unitid label=\"ArchonID\" audience=\"internal\">id6278</unitid>\n                    <unittitle>Music Scores and Parts</unittitle>\n                    <unitdate>1956-1960</unitdate>\n                  </did>\n                  <c02 level=\"file\">\n                    <did>\n                      <unitid label=\"ArchonID\" audience=\"internal\">id1970</unitid>\n                      <container type=\"Box\">1</container>\n                    </did>\n                    <c03 level=\"file\">\n                      <did>\n                        <unitid label=\"ArchonID\" audience=\"internal\">id1047</unitid>\n                        <container type=\"Box\">1</container>\n                        <container type=\"Folder\">1</container>\n                        <unittitle>\"La fiesta de San AntÃ³n\" (Torregosa)</unittitle>\n                        <unitdate>1957</unitdate>\n                      </did>\n                    </c03>\n                    <c03 level=\"file\">\n                      <did>\n                        <unitid label=\"ArchonID\" audience=\"internal\">id1048</unitid>\n                        <container type=\"Box\">1</container>\n                        <container type=\"Folder\">2</container>\n                        <unittitle>\"Granada\" (Lara)</unittitle>\n                        <unitdate>1957</unitdate>\n                      </did>\n                    </c03>\n                    <c03 level=\"file\">\n           